const Search = ({children}) => {
    const [query, setQuery] = React.useState('');
    const handleQuery = e => {
        setQuery(e.target.value);
    }
    return(
        <>
            <div>
                {children}
                <input type="text" value={query} onChange={handleQuery}/>
            </div>
        </>
    )
}

export default Search;
